// Based on the Compass example provided by
// the Sense HAT python library
// https://github.com/RPi-Distro/python-sense-hat/tree/master/examples

var led_loop = [4, 5, 6, 7, 15, 23, 31, 39, 47, 55, 63, 62, 61, 60, 59, 58, 57, 56, 48, 40, 32, 24, 16, 8, 0, 1, 2, 3];
var led_degree_ratio = led_loop.length / 360.0;
var dir = (360 - msg.payload.compass)%360;

function getXY(dir) {
    var led_index = Math.floor(led_degree_ratio * (dir%360));
    var offset = led_loop[led_index];
    return [offset % 8,Math.floor(offset / 8)];
}

var previous = context.get('previous');
msg.payload = '';

var position = getXY(dir);
if (!previous || position[0] != previous[0][0] || position[1] != previous[0][1]) {
    if (previous) {
        msg.payload = previous[0][0]+","+previous[0][1]+",off,"+
                      previous[1][0]+","+previous[1][1]+",off,";
    } else {
        msg.payload = "*,*,off,";
    }
    previous = [
        position,
        getXY(dir+180)
    ];
    msg.payload += previous[0][0]+","+previous[0][1]+",red,"+
                  previous[1][0]+","+previous[1][1]+",white"

    context.set('previous',previous);
    return msg;
}
return null;
